        <div className={styles.content}>
          <h1 className={styles.title}>{t('hero.title')}</h1>
          <p className={styles.tagline}>{t('hero.tagline')}</p>
        </div>
        <div className={styles.videoContainer}>
          <div className={styles.videoPlaceholder}>
