import React from 'react';
import { useLanguage } from '../../context/LanguageContext';
import styles from './Screenshots.module.css';

const Screenshots = () => {
  const { t, isRTL } = useLanguage();

  const screens = [
    { key: 'farmer', icon: 'üßë‚Äçüåæ' },
    { key: 'investor', icon: 'üíº' },
    { key: 'expert', icon: 'üéì' }
  ];

  return (
    <section className={`${styles.screenshots} ${isRTL ? styles.rtl : ''}`} id="screenshots">
      <div className={styles.container}>
        <h2 className={styles.sectionTitle}>{t('screenshots.title')}</h2>

        <div className={styles.grid}>
          {screens.map((screen) => (
            <div key={screen.key} className={styles.screenshotCard}>
              <div className={styles.screenshotFrame}>
                <div className={styles.frameBrowser}>
                  <span className={styles.browserDot}></span>
                  <span className={styles.browserDot}></span>
                  <span className={styles.browserDot}></span>
                </div>
                <div className={styles.placeholder}>
                  <span className={styles.placeholderIcon}>{screen.icon}</span>
                  <span className={styles.placeholderText}>
                    {t('screenshots.placeholder')}
                  </span>
                </div>
              </div>
              <p className={styles.caption}>{t(`screenshots.${screen.key}`)}</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Screenshots;
