import React from 'react';
import { useLanguage } from '../../context/LanguageContext';
import type { TeamMember } from '../../types';
import styles from './Team.module.css';

/** Team members data */
const TEAM_MEMBERS: TeamMember[] = [
  { name: 'Ali Boutabout' },
  { name: 'Bouremana Sara' },
  { name: 'Abusaada Mohammed' },
  { name: 'Benseba Nourhene' },
];

/**
 * Extracts initials from a full name
 */
const getInitials = (name: string): string => {
  return name
    .split(' ')
    .map((word) => word[0])
    .join('')
    .toUpperCase();
};

/**
 * Generates a consistent color based on name
 */
const getAvatarGradient = (index: number): string => {
  const gradients = [
    'linear-gradient(135deg, #22c55e 0%, #16a34a 100%)',
    'linear-gradient(135deg, #3b82f6 0%, #2563eb 100%)',
    'linear-gradient(135deg, #f59e0b 0%, #d97706 100%)',
    'linear-gradient(135deg, #8b5cf6 0%, #7c3aed 100%)',
  ];
  return gradients[index % gradients.length];
};

/**
 * Team section component
 * Displays founding team members with avatar initials
 */
const Team: React.FC = () => {
  const { t, isRTL } = useLanguage();

  return (
    <section 
      className={`${styles.team} ${isRTL ? styles.rtl : ''}`} 
      id="team"
      aria-labelledby="team-title"
    >
      <div className={styles.container}>
        <div className={styles.sectionHeader}>
          <span className={styles.sectionLabel}>The People Behind ProdNet</span>
          <h2 className={styles.sectionTitle} id="team-title">
            {t('team.title')}
          </h2>
        </div>

        <div className={styles.grid}>
          {TEAM_MEMBERS.map((member, index) => (
            <article key={member.name} className={styles.memberCard}>
              <div 
                className={styles.avatar}
                style={{ background: getAvatarGradient(index) }}
              >
                <span className={styles.initials}>{getInitials(member.name)}</span>
              </div>
              <p className={styles.name}>{member.name}</p>
            </article>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Team;
