import React from 'react';
import { useLanguage } from '../../context/LanguageContext';
import type { VisionFeatureItem } from '../../types';
import styles from './Vision.module.css';

/** Vision features configuration */
const FEATURES: VisionFeatureItem[] = [
  { key: 'verification', icon: 'ðŸ”' },
  { key: 'lifecycle', icon: 'ðŸ“Š' },
  { key: 'validation', icon: 'âœ…' },
  { key: 'scalability', icon: 'ðŸš€' },
];

/**
 * Vision section component
 * Presents future roadmap features
 */
const Vision: React.FC = () => {
  const { t, isRTL } = useLanguage();

  /**
   * Formats index as a two-digit string (01, 02, etc.)
   */
  const formatNumber = (index: number): string => {
    return String(index + 1).padStart(2, '0');
  };

  return (
    <section 
      className={`${styles.vision} ${isRTL ? styles.rtl : ''}`} 
      id="vision"
      aria-labelledby="vision-title"
    >
      <div className={styles.container}>
        <div className={styles.sectionHeader}>
          <span className={styles.sectionLabel}>Looking Ahead</span>
          <h2 className={styles.sectionTitle} id="vision-title">
            {t('vision.title')}
          </h2>
          <p className={styles.intro}>{t('vision.intro')}</p>
        </div>

        <div className={styles.grid}>
          {FEATURES.map((feature, index) => (
            <article key={feature.key} className={styles.featureCard}>
              <div className={styles.featureNumber} aria-hidden="true">
                {formatNumber(index)}
              </div>
              <div className={styles.featureContent}>
                <div className={styles.featureHeader}>
                  <span className={styles.featureIcon} aria-hidden="true">
                    {feature.icon}
                  </span>
                  <h3 className={styles.featureTitle}>
                    {t(`vision.features.${feature.key}.title`)}
                  </h3>
                </div>
                <p className={styles.featureDescription}>
                  {t(`vision.features.${feature.key}.description`)}
                </p>
              </div>
            </article>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Vision;
