import React from 'react';
import { useLanguage } from '../../context/LanguageContext';
import styles from './Overview.module.css';

const Overview = () => {
  const { t, isRTL } = useLanguage();

  return (
    <section className={`${styles.overview} ${isRTL ? styles.rtl : ''}`} id="overview">
      <div className={styles.container}>
        <h2 className={styles.sectionTitle}>{t('overview.title')}</h2>

        <div className={styles.grid}>
          <div className={styles.card}>
            <div className={styles.cardIcon}>‚ö†Ô∏è</div>
            <h3 className={styles.cardTitle}>{t('overview.problem.title')}</h3>
            <p className={styles.cardDescription}>
              {t('overview.problem.description')}
            </p>
          </div>

          <div className={`${styles.card} ${styles.solutionCard}`}>
            <div className={styles.cardIcon}>üí°</div>
            <h3 className={styles.cardTitle}>{t('overview.solution.title')}</h3>
            <p className={styles.cardDescription}>
              {t('overview.solution.description')}
            </p>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Overview;
