import React from 'react';
import { useLanguage } from '../../context/LanguageContext';
import styles from './Vision.module.css';

const Vision = () => {
  const { t, isRTL } = useLanguage();

  const features = [
    { key: 'verification', icon: 'üîê' },
    { key: 'lifecycle', icon: 'üìä' },
    { key: 'validation', icon: '‚úÖ' },
    { key: 'scalability', icon: 'üöÄ' }
  ];

  return (
    <section className={`${styles.vision} ${isRTL ? styles.rtl : ''}`} id="vision">
      <div className={styles.container}>
        <h2 className={styles.sectionTitle}>{t('vision.title')}</h2>
        <p className={styles.intro}>{t('vision.intro')}</p>

        <div className={styles.grid}>
          {features.map((feature, index) => (
            <div key={feature.key} className={styles.featureCard}>
              <div className={styles.featureNumber}>{String(index + 1).padStart(2, '0')}</div>
              <div className={styles.featureContent}>
                <div className={styles.featureHeader}>
                  <span className={styles.featureIcon}>{feature.icon}</span>
                  <h3 className={styles.featureTitle}>
                    {t(`vision.features.${feature.key}.title`)}
                  </h3>
                </div>
                <p className={styles.featureDescription}>
                  {t(`vision.features.${feature.key}.description`)}
                </p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Vision;
