import React from 'react';
import { useLanguage } from '../../context/LanguageContext';
import type { TeamMember } from '../../types';
import styles from './Team.module.css';

/** Team members data with roles */
const TEAM_MEMBERS: (TeamMember & { role: string; emoji: string })[] = [
  { name: 'Ali Boutabout', role: 'Project Lead', emoji: 'ðŸ‘¨â€ðŸ’»' },
  { name: 'Bouremana Sara', role: 'UI/UX Designer', emoji: 'ðŸŽ¨' },
  { name: 'Abusaada Mohammed', role: 'Backend Dev', emoji: 'âš™ï¸' },
  { name: 'Benseba Nourhene', role: 'Data Analyst', emoji: 'ðŸ“Š' },
];

/**
 * Generates a consistent color based on index
 */
const getMemberColor = (index: number): string => {
  const colors = ['#22c55e', '#3b82f6', '#f59e0b', '#8b5cf6'];
  return colors[index % colors.length];
};

/**
 * Team section component
 * Displays founding team in a collaborative horizontal layout
 */
const Team: React.FC = () => {
  const { t, isRTL } = useLanguage();

  return (
    <section 
      className={`${styles.team} ${isRTL ? styles.rtl : ''}`} 
      id="team"
      aria-labelledby="team-title"
    >
      <div className={styles.container}>
        <div className={styles.content}>
          {/* Left side - Title and description */}
          <div className={styles.info}>
            <span className={styles.badge}>ðŸ‘¥ {t('team.title')}</span>
            <h2 className={styles.title} id="team-title">
              Meet the <span className={styles.highlight}>Founders</span>
            </h2>
            <p className={styles.description}>
              A passionate team of computer science students from the University of Batna 2, 
              working together to revolutionize agricultural commerce in Algeria.
            </p>
          </div>

          {/* Right side - Team members */}
          <div className={styles.members}>
            {TEAM_MEMBERS.map((member, index) => (
              <div 
                key={member.name} 
                className={styles.member}
                style={{ '--accent': getMemberColor(index) } as React.CSSProperties}
              >
                <div className={styles.avatar}>
                  <span className={styles.emoji}>{member.emoji}</span>
                </div>
                <div className={styles.details}>
                  <h3 className={styles.name}>{member.name}</h3>
                  <span className={styles.role}>{member.role}</span>
                </div>
                <div 
                  className={styles.indicator}
                  style={{ background: getMemberColor(index) }}
                />
              </div>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default Team;
