import React from 'react';
import { useLanguage } from '../../context/LanguageContext';
import styles from './Overview.module.css';

/**
 * Overview section component
 * Presents the problem and solution in a two-card layout
 */
const Overview: React.FC = () => {
  const { t, isRTL } = useLanguage();

  return (
    <section 
      className={`${styles.overview} ${isRTL ? styles.rtl : ''}`} 
      id="overview"
      aria-labelledby="overview-title"
    >
      <div className={styles.container}>
        <div className={styles.sectionHeader}>
          <span className={styles.sectionLabel}>Understanding the Need</span>
          <h2 className={styles.sectionTitle} id="overview-title">
            {t('overview.title')}
          </h2>
        </div>

        <div className={styles.grid}>
          {/* Problem Card */}
          <article className={styles.card}>
            <div className={styles.cardIconWrapper}>
              <span className={styles.cardIcon} aria-hidden="true">‚ö†Ô∏è</span>
            </div>
            <div className={styles.cardContent}>
              <h3 className={styles.cardTitle}>{t('overview.problem.title')}</h3>
              <p className={styles.cardDescription}>
                {t('overview.problem.description')}
              </p>
            </div>
          </article>

          {/* Solution Card */}
          <article className={`${styles.card} ${styles.solutionCard}`}>
            <div className={styles.cardIconWrapper}>
              <span className={styles.cardIcon} aria-hidden="true">üí°</span>
            </div>
            <div className={styles.cardContent}>
              <h3 className={styles.cardTitle}>{t('overview.solution.title')}</h3>
              <p className={styles.cardDescription}>
                {t('overview.solution.description')}
              </p>
            </div>
          </article>
        </div>
      </div>
    </section>
  );
};

export default Overview;
