import React from 'react';
import { useLanguage } from '../../context/LanguageContext';
import type { ScreenshotScreen } from '../../types';
import styles from './Screenshots.module.css';

/** Screenshot screens configuration */
const SCREENS: ScreenshotScreen[] = [
  { key: 'farmer', icon: 'ðŸ§‘â€ðŸŒ¾' },
  { key: 'investor', icon: 'ðŸ’¼' },
  { key: 'expert', icon: 'ðŸŽ“' },
];

/**
 * Screenshots section component
 * Displays placeholder mockups for each user type's interface
 */
const Screenshots: React.FC = () => {
  const { t, isRTL } = useLanguage();

  return (
    <section 
      className={`${styles.screenshots} ${isRTL ? styles.rtl : ''}`} 
      id="screenshots"
      aria-labelledby="screenshots-title"
    >
      <div className={styles.container}>
        <div className={styles.sectionHeader}>
          <span className={styles.sectionLabel}>Product Preview</span>
          <h2 className={styles.sectionTitle} id="screenshots-title">
            {t('screenshots.title')}
          </h2>
        </div>

        <div className={styles.grid}>
          {SCREENS.map((screen) => (
            <figure key={screen.key} className={styles.screenshotCard}>
              <div className={styles.screenshotFrame}>
                {/* Browser chrome decoration */}
                <div className={styles.frameBrowser} aria-hidden="true">
                  <span className={styles.browserDot} />
                  <span className={styles.browserDot} />
                  <span className={styles.browserDot} />
                  <span className={styles.browserUrl}>prodnet.app/{screen.key}</span>
                </div>
                {/* Placeholder content */}
                <div className={styles.placeholder}>
                  <span className={styles.placeholderIcon} aria-hidden="true">
                    {screen.icon}
                  </span>
                  <span className={styles.placeholderText}>
                    {t('screenshots.placeholder')}
                  </span>
                </div>
              </div>
              <figcaption className={styles.caption}>
                {t(`screenshots.${screen.key}`)}
              </figcaption>
            </figure>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Screenshots;
