import React from 'react';
import { useLanguage } from '../../context/LanguageContext';
import styles from './Actors.module.css';

const Actors = () => {
  const { t, isRTL } = useLanguage();

  const actors = [
    {
      key: 'farmer',
      icon: 'üßë‚Äçüåæ',
      color: '#4CAF50'
    },
    {
      key: 'investor',
      icon: 'üíº',
      color: '#2196F3'
    },
    {
      key: 'expert',
      icon: 'üéì',
      color: '#FF9800'
    }
  ];

  return (
    <section className={`${styles.actors} ${isRTL ? styles.rtl : ''}`} id="actors">
      <div className={styles.container}>
        <h2 className={styles.sectionTitle}>{t('actors.title')}</h2>

        <div className={styles.grid}>
          {actors.map((actor) => (
            <div 
              key={actor.key} 
              className={styles.card}
              style={{ '--accent-color': actor.color }}
            >
              <div className={styles.cardHeader}>
                <span className={styles.icon}>{actor.icon}</span>
                <h3 className={styles.cardTitle}>
                  {t(`actors.${actor.key}.title`)}
                </h3>
              </div>
              <div className={styles.cardBody}>
                <p className={styles.role}>
                  {t(`actors.${actor.key}.role`)}
                </p>
                <div className={styles.divider}></div>
                <p className={styles.value}>
                  {t(`actors.${actor.key}.value`)}
                </p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Actors;
