import React from 'react';
import { useLanguage } from '../../context/LanguageContext';
import type { Actor, ActorKey } from '../../types';
import styles from './Actors.module.css';

/** Platform actors configuration */
const ACTORS: Actor[] = [
  { key: 'farmer', icon: 'ðŸ§‘â€ðŸŒ¾', color: '#22c55e' },
  { key: 'investor', icon: 'ðŸ’¼', color: '#3b82f6' },
  { key: 'expert', icon: 'ðŸŽ“', color: '#f59e0b' },
];

/**
 * Actors section component
 * Displays the three platform stakeholder types
 */
const Actors: React.FC = () => {
  const { t, isRTL } = useLanguage();

  return (
    <section 
      className={`${styles.actors} ${isRTL ? styles.rtl : ''}`} 
      id="actors"
      aria-labelledby="actors-title"
    >
      <div className={styles.container}>
        <div className={styles.sectionHeader}>
          <span className={styles.sectionLabel}>Who It's For</span>
          <h2 className={styles.sectionTitle} id="actors-title">
            {t('actors.title')}
          </h2>
        </div>

        <div className={styles.grid}>
          {ACTORS.map((actor) => (
            <article
              key={actor.key}
              className={styles.card}
              style={{ '--accent-color': actor.color } as React.CSSProperties}
            >
              <div className={styles.cardHeader}>
                <span className={styles.icon} aria-hidden="true">{actor.icon}</span>
                <h3 className={styles.cardTitle}>
                  {t(`actors.${actor.key}.title`)}
                </h3>
              </div>
              <div className={styles.cardBody}>
                <p className={styles.role}>
                  {t(`actors.${actor.key}.role`)}
                </p>
                <div className={styles.divider} aria-hidden="true" />
                <p className={styles.value}>
                  {t(`actors.${actor.key}.value`)}
                </p>
              </div>
            </article>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Actors;
